func CreateMap(tileid m.TileID) string {
	filename := os.Getenv("MAPBOX_STYLE")
	if filename == "" {
		fmt.Println("Enviromental variable MAPBOX_STYLE not set please set MAPBOX_STYLE to a filename referencing a style json.")
	}
	bytevals, _ := ioutil.ReadFile(filename)
	center := m.Center(tileid)

	html := fmt.Sprintf(string(template), center[0], center[1], string(bytevals), int(tileid.Z))

	html = strings.Replace(html, "####", "%", -1)
	return html
}

func Serve(gvt util.Mbtiles, html string, tileid m.TileID) *http.Server {

	// creating mutex as this process does need to block as its drilling tiles.
	var mm sync.Mutex
	srv := &http.Server{Addr: ":8080"}
	test := "Test"
	tile := &vector_tile.Tile{Layers: []*vector_tile.Tile_Layer{{Name: &test}}}
	bs, _ := proto.Marshal(tile)
	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
		mm.Lock()
		w.Header().Set("Access-Control-Allow-Origin", "*")
		vals := strings.Split(r.URL.Path, "/")
		if len(vals) == 2 {
			if vals[1] == "index.html" {
				fmt.Fprintf(w, "%s", string(html))
			} else {
				fmt.Fprintf(w, "%s", string(bs))
			}

		} else if len(vals) == 4 {
			// getting x,y,z tile
			z, x, y := vals[1], vals[2], vals[3]
			znew, _ := strconv.ParseInt(z, 10, 64)
			xnew, _ := strconv.ParseInt(x, 10, 64)
			ynew, _ := strconv.ParseInt(y, 10, 64)
			//ynew = int64((1 << uint64(znew)) - 1 - ynew)
			if int(tileid.X) == int(xnew) && int(tileid.Y) == int(ynew) && int(tileid.Z) == int(znew) {
				s := time.Now()
				data, _ := gvt.Query(m.TileID{xnew, ynew, uint64(znew)})
				fmt.Fprintf(w, "%s", string(data))
				fmt.Println(x+"/"+y+"/"+z, time.Now().Sub(s), "served tile", tileid)
			} else {
				fmt.Fprintf(w, "%s", string(bs))
			}

		} else {
			fmt.Fprintf(w, "%s", string(bs))

		}
		mm.Unlock()
	})
	go func() {
		if err := srv.ListenAndServe(); err != nil {
			// cannot panic, because this probably is an intentional close
			//log.Printf("Httpserver: ListenAndServe() error: %s", err)
		}
	}()
	return srv

}

func StartServer(mbfilename string, tileid m.TileID) {
	gvt, _ := util.ReadMbtiles(mbfilename)
	html := CreateMap(tileid)
	srv := Serve(gvt, html, tileid)
	time.Sleep(1 * time.Second)
	cmd := exec.Command("open", "http://127.0.0.1:8080/index.html")
	cmd.Run()
	time.Sleep(5 * time.Second)

	if err := srv.Shutdown(nil); err != nil {
	}

}

